import React from 'react';
import styles from "./Users.module.css";


let Users = (props) => {

    axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response);
    
    if(props.users.length === 0){
    props.setUsers(
        [
            { id: 1, followed: false, photoUrl:'https://foma.ru/fotos/journal/113/emelyanenko_1.jpg', fullName: 'Vasya', status: 'sss', location: { city: 'Moscow', country: 'Russia' } },
            { id: 2, followed: false, photoUrl:'https://i0.wp.com/bookmaker-ratings.ru/wp-content/uploads/2019/10/Konor-Makgregor-6-1.jpg?fit=800%2C450&ssl=1', fullName: 'Konnor', status: 'sss', location: { city: 'Dublin', country: 'Ireland' }}
            
        ]
    )}


    return <div>
                { 
                    props.users.map(u => <div key={u.id}>
                    <span>
                            <div>
                                <img src={u.photoUrl} className={styles.userPhoto}/>
                            </div>
                            <div>
                                
                                {u.followed
                                    ?<button onClick={()=>{props.unfollow(u.id)}}> Подписаться </button>
                                    :<button onClick={()=>{props.follow(u.id)}}> Вы подписаны </button>
                                }
                            </div>
                    </span>
                    <span>
                        <span>
                            <div>{u.fullName}</div>
                            <div>{u.status}</div>
                        </span>
                        <span>
                            <div>{u.location.city}</div>
                            <div>{u.location.country}</div>
                        </span>
                    </span>
                    </div>)
                }
            </div>
};

export default Users;
