export const required = value => {
    if (value) return undefined;

    return 'Field is required';
}

export const maxLengthCreator = (maxLength) => (value) => {
    if (value.length > maxLength) return  'Max length is' + <span>{maxLength}</span> + 'symbols';
    return undefined;
}
