import React from 'react';
import styles from "./Users.module.css";


const Users = (props) => {

    if(props.users)
    props.setUsers ( [
        { id: 1, photoUrl: 'https://foma.ru/fotos/journal/113/emelyanenko_1.jpg', followed: false, fullName: "Fedor", Status: 'Be Happy', location: { city: 'Kiev', country: 'Ukraune' } },
        { id: 2, photoUrl: 'https://foma.ru/fotos/journal/113/emelyanenko_1.jpg', followed: false, fullName: "Fedya", Status: 'Be Happy', location: { city: 'Kiev', country: 'Ukraune' } },
        { id: 3, photoUrl: 'https://foma.ru/fotos/journal/113/emelyanenko_1.jpg', followed: false, fullName: "FEDOT", Status: 'Be Happy', location: { city: 'Kiev', country: 'Ukraune' } },
        { id: 4, photoUrl: 'https://foma.ru/fotos/journal/113/emelyanenko_1.jpg', followed: false, fullName: "FEDOR", Status: 'Be Happy', location: { city: 'Kiev', country: 'Ukraune' } },
    ]);

    return <div>
                { 
                    props.users.map(u => <div key={u.id}>
                    <span>
                            <div>
                                <img src={u.photoUrl} className={styles.usersPhoto}/>
                            </div>
                            <div>
                                
                                {u.followed
                                    ?<button onClick={()=>{props.unfollow(u.id)}}> Подписаться </button>
                                    :<button onClick={()=>{props.follow(u.id)}}> Вы подписаны </button>
                                }
                            </div>
                    </span>
                    <span>
                        <span>
                            <div>{u.fullName}</div>
                            <div>{u.status}</div>
                        </span>
                        <span>
                            <div>{u.location.city}</div>
                            <div>{u.location.country}</div>
                        </span>
                    </span>
                    </div>)
                }
            </div>
};

export default Users;
